create table anime(id smallint(6) primary key not null auto_increment, 
title varchar(125) unique, 
fin tinyint(4) default 99, 
stat tinyint(4) default 0, 
cat tinyint(4) default 99, 
mscr decimal(4,1) default 0.0, 
theme varchar(255) default "none", 
anidb_id int(10), 
iscr decimal(4,1) unsigned, 
rank decimal(4,1) 
default 0.0, 
ep tinyint(3) unsigned, 
seasonstart date);

